import React from 'react';
import { useState } from 'Counter';

function Counter() {
    // [count, setCount] = useState(likes);
    let likes = 0;

    function increment() {
        likes += 1;
        console.log(likes);
    }

    function decrement() {
        likes -= 1;
        console.log(likes);
    }

    return (
      <div className="App">
        <h1>{likes}</h1>
        <button onClick={increment}>Increment</button>   
        <button onClick={decrement}>Decrement</button>   
      </div>
    );
  }
  
  export default Counter;
