import React, { useState } from 'react'
import './Slider.css';


const Slider = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    // const sliderItems = [
    //     { id: 1, title: 'video 1' },
    //     { id: 2, title: 'video 2' },
    //     { id: 3, title: 'video 3' },
    //     { id: 4, title: 'video 4' },
    //     { id: 5, title: 'video 5' },
    // ];
    const handleSpin = () => {
        setActiveIndex((prevIndex) => (prevIndex + 1) % sliderItems.length);
    };

    return (
      <div className="slider">
        <button onClick={handleSpin} className="spin-button">
          Spin
        </button>

        <div className="slider-item" style={{ backgroundColor: 'red' }}></div>
        <div className="slider-item" style={{ backgroundColor: 'blue' }}></div>
        <div className="slider-item" style={{ backgroundColor: 'green' }}></div>
        <div className="slider-item" style={{ backgroundColor: 'yellow' }}></div>
        <div className="slider-item" style={{ backgroundColor: 'orange' }}></div>

        {sliderItems.map((item, index) => (
            <div 
            key={item.id}
            value={item.title}
            className={`slider-item ${index === activeIndex ? 'active' : ''}
            `}>
              <h3>{item.title}</h3>
            </div>
        ))};
      </div>
    );
}

export default Slider;
